import React, { Component } from "react";
 import "./shoppingIndex.scss";
import { inject, observer } from "mobx-react";


@inject("card")
@observer
class ShoppingIndex extends Component {
  constructor() {
    super();
    this.state = {
      isFinish: true, //默认的是完成页面
      isChecked: true //判断有没有选中
    };
  }

  componentDidMount() {
    //获取购物车里面的数据
    this.props.card.getCartDataModule();
    //获取页面全选的数据
    this.props.card.changeInitFinishCheckedFn();
  }

  //切换页面
  changeEdit = e => {
    this.setState({
      isFinish: !this.state.isFinish
    });
  };

//切换完成页面的选中的按钮
  changeChecked(data){
    // isChecked: 1,
    // productIds: productIdsData
    // console.log(data);
    let checked=data.checked===0?1:0;
    this.props.card.postCartCheckModule({
         isChecked: checked,
     productIds: data.product_id
    })
    
  }

  

  render() {
    let data = this.props.card.getCartData;
    // console.log(data);
    //postCartCheckData
   // let {postCartCheckData}=this.props.card;
    //console.log(postCartCheckData);

   // let { isFinish } = this.state;
    return (
      <div className="shopBox">
        {/* 头部 */}
        <ul className="service">
          <li>
            <span>★</span>
            30天无忧退货
          </li>
          <li>
            <span>★</span>
            48小时快速退款
          </li>
          <li>
            <span>★</span>
            满88包邮
          </li>
        </ul>
        {/* 内容 */}
        <div className="main1">
          {data.cartList &&
            data.cartList.map((item) => {
              return (
              
                  <div className="shopFinshBox" key={item.goods_name}>
                   {
                     this.state.isFinish?<div className="check" onClick={()=>{this.changeChecked(item)}}>
                     <img
                       src={
                         item.checked === 1
                           ? "data:image/png;base64,"
                           : "data:image/png;base64,"
                       }
                       alt=""
                     />
                   </div>:<div className="check" onClick={()=>{this.props.card.changeEditItemChecked(item)}}>
                     <img
                       src={
                         item.flag
                           ? "data:image/png;base64,"
                           : "data:image/png;base64,"
                       }
                       alt=""
                     />
                   </div>

                   } 
                    <div className="img">
                      <img src={item.list_pic_url} alt="" />
                    </div>

                    {this.state.isFinish ? (
                      <div className="shopFinshBox_main">
                        <div className="shopFinshMain_top">
                          <h3>{item.goods_name}</h3>
                          <div className="num">
                            x<span>{item.number}</span>
                          </div>
                        </div>
                        <div className="price">￥{item.market_price}</div>
                      </div>
                    ) : (
                      <div className="itemBox_main">
                       
                        <div className="title">{item.goods_name}</div>
                        <div className="numPrice">
                          <div className="price">￥{item.market_price}</div>

                          <div className="numBox">
                            <span>-</span>
                            <span className="num">{item.number}</span>
                            <span>+</span>
                          </div>
                        </div>
                      </div>
                    )}
                  </div>
                
              );
            })}
        </div>
        {/* 选项 */}
        <div className="shop">
          <div className="checkedBox">
            <div className="choice">
              {this.state.isFinish? <div
                className="check"
                onClick={() => this.props.card.changeChecked()}
              >
                <img
                  src={
                    this.props.card.FinishIsChecked
                      ? "data:image/png;base64,"
                      : "data:image/png;base64,"
                  }
                  alt=""
                />
              </div>: <div
                className="check"
                onClick={() => this.props.card.changeEditChecked()}
              >
                <img
                  src={
                    this.props.card.EditIsChecked
                      ? "data:image/png;base64,"
                      : "data:image/png;base64,"
                  }
                  alt=""
                />
              </div>}
             
              {this.state.isFinish ?<div>已选{data.cartTotal&&data.cartTotal.checkedGoodsCount}商品</div>:<div>已选择{this.props.card.EditCheckedCount}</div>}
              {this.state.isFinish ? <div>￥{data.cartTotal&&data.cartTotal.checkedGoodsAmount}</div> : null}
            </div>
          </div>

          <div className="edit" onClick={e => this.changeEdit(e)}>
            {this.state.isFinish ? "编辑" : "完成"}
          </div>

          <div className="option">
            {this.state.isFinish ? "下单" : "删除所选"}
          </div>
        </div>
      </div>
    );
  }
}

export default ShoppingIndex;
